	        var uri = request.url;
	        var matrixString = 'teams;team_keys=';
	        for (var i = 0; i < team_key.length; i++) {
          matrixString += team_key[i];
	          if (i < team_key.length -1) matrixString +=',';
	        }
        matrixString += '/stats';

	        if (stats_type) matrixString+= ';type=' + stats_type;
	        if (stats_date) matrixString += ';date=' + stats_date;
	        if (stats_week) matrixString += ';week=' + stats_week;
	        if (stats_season) matrixString += ';season=' + stats_season;

	        var appauth = request.headers['Yahoo-App-Auth'];
	        var req = y.rest(uri).path(matrixString).header('Authorization', internal.auth.authorization);
        if (appauth) req.header('Yahoo-App-Auth', appauth);

	        response.object = req.get().response;
